import { MapContainer, TileLayer } from 'react-leaflet';
import { useEffect, useState } from "react";
import './App.css';

const WeatherMap = () => {
    const [mapUrl, setMapUrl] = useState('');

    useEffect(() => {
        // Construct the API URL based on your requirements
        const apiUrl = 'http://maps.openweathermap.org/maps/2.0/weather/PA0/9/0/0?appid=fca1d839270b44785afc07f9cb794775'; // Replace with your actual API URL
        console.log('API URL:', apiUrl);
        setMapUrl(apiUrl);
    }, []);

    return (
        <MapContainer
            center={[0, 0]}
            zoom={2}
            style={{ height: '500px', width: '100%' }}
        >
            <TileLayer url={mapUrl} />
        </MapContainer>
    );
};

const WeatherComponent = (props) => {
    const { city, temperature, conditions, weathericon, lon, lat, onDelete } = props;
    const [isExpanded, setIsExpanded] = useState(false);
    const iconBaseUrl = "https://openweathermap.org/img/wn/";

    const handleDelete = () => {
        onDelete();
    }

    const center = {
        lat: parseFloat(lat),
        lng: parseFloat(lon),
    };

    return (
        <div className="weatherDiv" onClick={() => setIsExpanded(!isExpanded)}>
            <p layout="position" className="city">{city}</p>
            <p layout="position" className="temperature">{temperature + '\u00B0'}</p>
            <p className="conditions">
                <span>{conditions}</span>
                <img className="weatherIcon" src={`${iconBaseUrl}${weathericon}@2x.png`} alt="Weather Icon" />
            </p>

            {isExpanded && (
                <div className="expandedDiv">
                    <div className="showmap">
                        <WeatherMap />
                    </div>
                </div>
            )}

            <button className="delete" onClick={onDelete}>-</button>
        </div>
    );
};

export default WeatherComponent;
