import React, { useState, useEffect } from "react";
import WeatherComponent from './weathercomponent';
import './App.css';

function Weather() {
  const api = {
    key: 'fca1d839270b44785afc07f9cb794775',
    base: 'https://api.openweathermap.org/data/2.5',
  };

  const [weatherData, setWeatherData] = useState({});
  const [city, setCity] = useState("");
  const [componentInstances, setComponentInstances] = useState([]);

  // Load saved weather data from localStorage on component mount
  useEffect(() => {
    const savedWeatherData = JSON.parse(localStorage.getItem("weatherData"));
    if (savedWeatherData) {
      setWeatherData(savedWeatherData);
    }

    // Listen for popstate event (back navigation)
    const handlePopstate = () => {
      const updatedWeatherData = JSON.parse(localStorage.getItem("weatherData"));
      if (updatedWeatherData) {
        setWeatherData(updatedWeatherData);
      }
    };

    window.addEventListener("popstate", handlePopstate);

    return () => {
      window.removeEventListener("popstate", handlePopstate);
    };
  }, []);

  // Save weather data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("weatherData", JSON.stringify(weatherData));
  }, [weatherData]);

  const getWeather = (event) => {
    if (event.key === "Enter") {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api.key}&units=imperial`)
        .then(response => response.json())
        .then(data => {
          setWeatherData(data);
          setCity("");

          // Create a new Weather component and add it to the instances
          const newWeatherComponent = (
            <WeatherComponent
              prop1={data.name}
              prop2={Math.round(data.main.temp)}
              prop3={data.weather[0].main}
              onDelete={() => (handleDelete(componentInstances.length))}
            />
          );

          setComponentInstances([...componentInstances, newWeatherComponent]);
        });
    }
  };

  const handleDelete = (index) => {
    const updatedInstances = [...componentInstances];
    updatedInstances.splice(index, 1);
    setComponentInstances(updatedInstances);
  }

  return (
    <div className="App">
      <input
        className="input"
        placeholder="Enter Location"
        onChange={(e) => setCity(e.target.value)}
        value={city}
        onKeyDown={getWeather}
      />

      {typeof weatherData.main === 'undefined' && (
        <div>
          <p>Welcome to WeatherApp. Enter a city to get started</p>
        </div>
      )}

      {componentInstances.map((component, index) => (
        React.cloneElement(component, { onDelete: () => handleDelete(index) })
      ))}
    </div>
  );
}

export default Weather;
