import React from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import { useEffect, useState } from "react";


const WeatherMap = () => {
    const [mapUrl, setMapUrl] = useState('');

    useEffect(() => {
        // Example coordinates for a specific location (adjust as needed)
        const lat = 40.7128; // Example latitude (New York City)
        const lon = -74.0060; // Example longitude (New York City)

        // Calculate zoom level based on the desired map bounds
        const zoom = 10; // Adjust as needed
        const tileSize = 256; // Standard tile size

        const x = Math.floor(((lon + 180) / 360) * (2 ** zoom));
        const y = Math.floor(((1 - Math.log(Math.tan((lat * Math.PI) / 180) + 1 / Math.cos((lat * Math.PI) / 180)) / Math.PI) / 2) * (2 ** zoom));

        // Construct the API URL based on your requirements
        const apiUrl = `http://maps.openweathermap.org/maps/2.0/weather/PA0/${zoom}/${x}/${y}?appid=fca1d839270b44785afc07f9cb794775`;

        console.log('API URL:', apiUrl);
        setMapUrl(apiUrl);
    }, []);

    return (
        <MapContainer
            center={[0, 0]}
            zoom={2}
            style={{ height: '500px', width: '100%' }}
        >
            <TileLayer url={mapUrl} />
        </MapContainer>
    );
};

export default WeatherMap;
