import React, { useState } from "react";
import './App.css'



function App() {

  const api = {
    key: 'fca1d839270b44785afc07f9cb794775',
    base: 'https://api.openweathermap.org/data/2.5',
  }
  const [weatherData, setWeatherData] = useState([{}]);
  const[city, setCity] = useState("");

  const getWeather = (event) => {
    if (event.key == "Enter") {
      fetch('https://api.openweathermap.org/data/2.5/weather?lat={city}&lon={lon}&appid={api.key}')
      .then(
        response => response.json()
      ).then(
        data => {
          setWeatherData(data)
        }
      )
    }
  }

  return(
    <div className="App">
      <input 
      className="input" 
      placeholder="Enter Location">
      onChange={e => setCity(e.target.value)}
      value={city}
      onKeyPress={getWeather}
      </input>
    </div>
  );

}

export default App;
