import React, { useState } from "react";
import { v4 as uuidv4 } from 'uuid';

import Test from './test';
import './App.css';

function App() {
  const api = {
    key: 'fca1d839270b44785afc07f9cb794775',
    base: 'https://api.openweathermap.org/data/2.5',
  };
  const [weatherData, setWeatherData] = useState({});
  const [city, setCity] = useState("");
  const [componentInstances, setComponentInstances] = useState([]);

  const getWeather = (event) => {
    if (event.key === "Enter") {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api.key}&units=imperial`)
        .then(response => response.json())
        .then(data => {
          setWeatherData(data);
          setCity("");

          // Create a new Weather component and add it to the instances
          const newWeatherComponent = (
            <Router>
              <Test
                prop1={data.name}
                prop2={Math.round(data.main.temp)}
                prop3={data.weather[0].main}
                onDelete={() => (handelDelete(componentInstances.length))}
              />
            </Router>

          );
          setComponentInstances([...componentInstances, newWeatherComponent]);
        });
    }
  };

  const handelDelete = (index) => {
    const updatedInstances = [...componentInstances];
    updatedInstances.splice(index, 1);
    setComponentInstances(updatedInstances);
  }

  return (
    <div className="App">
      <input
        className="input"
        placeholder="Enter Location"
        onChange={(e) => setCity(e.target.value)}
        value={city}
        onKeyDown={getWeather}
      />

      {typeof weatherData.main === 'undefined' && (
        <div>
          <p>Welcome to WeatherApp. Enter a city to get started</p>
        </div>
      )}

      {componentInstances.map((component, index) => (
        React.cloneElement(component, { onDelete: () => handelDelete(index) })
      ))}
    </div>
  );
}

export default App;
