import React from "react";
// import { useNavigate} from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion"
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import { useState } from "react";
import './App.css'


const WeatherComponent = (props) => {
    const { city, temperature, conditions, weathericon, lon, lat, forecastDataList, onDelete } = props;
    // const navigate = useNavigate();
    const [isExpanded, setIsExpanded] = useState(false);
    const iconBaseUrl = "https://openweathermap.org/img/wn/";

    const handleDelete = () => {
        onDelete();
    }
    const libraries = ['places'];
    const mapContainerStyle = {
        width: '450px',
        height: '200px',
    };

    const center = {
        lat: parseFloat(lat),
        lng: parseFloat(lon),
    };

    const { isLoaded, loadError } = useLoadScript({
        googleMapsApiKey: 'AIzaSyBp0RJ5wEOLY9e4ffdUihnFX6ItI1KdIcc',
        libraries,
    });

    if (loadError) {
        return <div>Error loading maps</div>;
    }

    if (!isLoaded) {
        return <div>Loading maps</div>;
    }

    return (

        <div className="weatherDiv" onClick={() => setIsExpanded(!isExpanded)}>
            <p className={`city ${isExpanded ? 'expandedCity' : ''}`}>{city}</p>
            <p className={`temperature ${isExpanded ? 'expandedTemp' : ''}`}>{temperature + '\u00B0'}</p>
            <div className={`test ${isExpanded ? 'expandedTest' : ''}`}>
                <p className={`conditions ${isExpanded ? 'expandedConditions' : ''}`}>
                    <span>{conditions}</span>
                    <img className="weatherIcon" src={`${iconBaseUrl}${weathericon}@2x.png`} alt="Weather Icon" />
                </p>

                <div className="gridWrapper">

                    <ul>
                        {forecastDataList.list.map((item, index) => (
                            <li key={index}>
                                <div className="forecastDiv">
                                    <p>Temp: {item.list.main[0]}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <div className="forecast">
                        <div className="forecastDiv">Day 1</div>
                        <div className="forecastDiv">Day 2</div>
                        <div className="forecastDiv">Day 3</div>
                        <div className="forecastDiv">Day 4</div>
                        <div className="forecastDiv">Day 5</div>
                    </div>

                </div>
            </div>



            {isExpanded && (
                <div className="expandedDiv">
                    <div className="map-container">
                        {/* <GoogleMap
                            className="map"
                            mapContainerStyle={mapContainerStyle}
                            zoom={9}
                            center={center}
                            options={{
                                disableDefaultUI: true,
                                zoomControl: false,
                            }}
                        /> */}
                    </div>
                </div>


            )}

            <button className="delete" onClick={onDelete}>-</button>
        </div>
    );
};


export default WeatherComponent;
