import React, { useState } from "react";

import Child from './test'
import './App.css'




const NewComponent =() => {
  return (
    <div>New Component</div>
  )
}

function App() {

  const api = {
    key: 'fca1d839270b44785afc07f9cb794775',
    base: 'https://api.openweathermap.org/data/2.5',
  }
  const [weatherData, setWeatherData] = useState([{}]);
  const[city, setCity] = useState("");

  const getWeather = (event) => {
    if (event.key == "Enter") {
      fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${api.key}&units=imperial`)
      .then(
        response => response.json()
      ).then(
        data => {
          setWeatherData(data)
          setCity("")
        }
      )
    }
  }

  const [showNewComponent, setShowNewComponent] = useState(false);

  const handleButtonClick = () => {
    setShowNewComponent(true);
  };

  return(
    <div className="App">
      <input
      className="input"
      placeholder="Enter Location"
      onChange={(e) => setCity(e.target.value)}
      value={city}
      onKeyDown={getWeather}
    />

    {typeof weatherData.main === 'undefined' ? (
      <div>
        <p>Welcome to WeatherApp. Enter a city to get started</p>
      </div>
    ): (
      <div>
        <p>{weatherData.name}</p>
        <p>{Math.round(weatherData.main.temp)}</p>
        <p>{weatherData.weather[0].main}</p>
      </div>
    )}

    <button onClick={handleButtonClick}>Create New Component</button>
      {showNewComponent && <NewComponent />}
    </div>
  );

}

export default App;
